(function($){$.fn.drag=function(o){var o=$.extend({start:function(){},stop:function(){}},o);return $(this).each(function(){var d=$(this);d.mousedown(function(e){d.css('position','absolute');$(document).unbind('mouseup');o.start(d);var f=d.offset(),x=e.pageX-f.left,y=e.pageY-f.top;$(document).mousemove(function(a){d.css({'top':a.pageY-y+'px','left':a.pageX-x+'px'})});$(document).mouseup(function(){$(document).unbind('mousemove');o.stop(d)});return false})})}})(jQuery);